$(document).ready(function(){"/BarberWeb/admin/agenda-cliente"===window.location.pathname&&$("#mostrarHorarios").on("click",function(){var r=$("#dataCliente").val(),e=$("#servicoCliente").val().split(";")[0];$.ajax({url:"/BarberWeb/admin/mostrar_horarios",type:"POST",data:{data:r,tempoServico:e},dataType:"json",success:function(r){r=r.horarios;$(".itensHorarios").empty(),$.each(r,function(r,e){r=`
                            <div class="item">
                                <div class="nomeBarbeiro">
                                    <img src="<?=URL_BASE.$_SESSION['usuario_logado']['foto_usuario']?>"> <!-- Ajuste a URL da imagem conforme necessário -->
                                    <p>${r}</p>
                                </div>
                                <div class="horariosBarbeiros">
                                    ${e.map(r=>`<span>${r}</span>`).join("")}
                                </div>
                            </div>
                        `;$(".itensHorarios").append(r)})},error:function(r,e,a){if(r.responseText)try{var o=JSON.parse(r.responseText);o.hasOwnProperty("error")?alert("Erro: "+o.error):alert("Ocorreu um erro na requisição.")}catch(r){alert("Ocorreu um erro na requisição: "+a)}else alert("Ocorreu um erro na requisição: "+a)}})})});